today.map:
function(doc) { 
  var expire = (new Date(doc.expires_in)),
    tdate = (new Date()),
    expire_date = expire.getFullYear() + expire.getMonth() + expire.getDate();
    today = tdate.getFullYear() + tdate.getMonth() + tdate.getDate() + 1;
  if (expire_date < today) {
    emit(doc._id, doc);
  }
}
