today.map:
function(doc) { 
  var expire = (new Date(doc.expires_in)),
    tdate = (new Date()),
    expire_date = expire.getFullYear() + ( 100 * expire.getMonth()) + expire.getDate();
    today = tdate.getFullYear() + ( 100 * tdate.getMonth()) + tdate.getDate();
  if (expire_date < today) {
    emit(doc._id, doc);
  }
}
